<script>
export default {
  mounted(){
    this.setClass()
  },
 props: {
    iconNameProps: String,
    whiteProps: Boolean,
    effectHoverProps: Boolean,
    activeEffectProps: Boolean,
    sizeProps: String, 
    indexIconProps: Number,
    iconClassNameProps: String,
    hoverProps: String,
    fillProps: String
 },
 data(){
    return {
        iconName: this.$props.iconNameProps,
        white: this.$props.whiteProps,
        activeEffect: this.$props.activeEffectProps,
        effectHover: this.$props.effectHoverProps,
        size: this.$props.sizeProps,
        indexIcon: this.$props.indexIconProps,
        iconClassName: this.$props.iconClassNameProps,
        fill: this.$props.fillProps,

    }
 },
 watch: {
  whiteProps(value){
    this.white = value
    console.log(value, this.iconName)
    if(value === false) {
      this.fill = this.$props.fillProps
    }
    else {
      this.fill = '#fff'
    }
  },
 },
 methods: {
    emitClick(){
      console.log('vou emitir isso aqui รณ', this.iconName)
        this.$emit('clicked', this.iconName)
    },
    iconClicked(){
      this.$emit('costumIconClicked', this.iconName)
    },
    mouseEnter(){
      if(this.effectHover){
        this.fill = this.$props.hoverProps
      }
    },
    mouseLeave(){
      if(this.effectHover && !this.white){
        this.fill = this.$props.fillProps
      }
    },
    setClass(){
      let iconContainer = document.querySelectorAll('[data-costumIcons]')
      console.log(iconContainer, 'outro console')
      iconContainer.forEach((icon, index)=> {
        console.log( icon, index, this.indexIcon)
        if(index === this.indexIcon) return icon.classList.add(this.iconClassName)

      })
        
      
    },
  },
}
</script>

